import { ChangeEvent, FormEvent, Fragment, useEffect, useState } from 'react';
import iFactura from '../../Interfaces/Interfaces';
import { Button, Col, Container, FloatingLabel, Form, Row } from 'react-bootstrap'
import InputLabel from '../InputLabel';
import SelectLabel from '../SelectLabel';
interface Props {
  addANewFactura: (factura: iFactura) => void;
}

type HandleInputChange = ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>;

const AltaFacturas = (prop: Props) => {
  //facturas
  const [factura, setFactura] = useState({
    IDComision: 0,
    NombrePrestador: "",
    CuitPrestador: "",
    NroPrestador: "",
    TipoFactura: "",
    ImporteFacturado: 0,
    NroFactura: ""
  });
  //prestadores de comisiones
  const [prestadores, setPrestadores] = useState(['elija una comision']);
  const [idPrestadores, setIdPrestadores] = useState([0]);
  const [prestador, setPrestador] = useState(0);
  //comisiones
  const [comision, setComision] = useState(0);

  //factura
  const handlerNewFactura = (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    prop.addANewFactura(factura);
  }

  const handleInputChange = ({

    target: { name, value },
  }: HandleInputChange) => {
    setFactura({
      ...factura,
      [name]: value,
    });
  }

  //comisiones

  const handleComisionChange = ({

    target: { name, value },
  }: HandleInputChange) => {
    setComision(Number.parseInt(value));
  }

  //prestador
  const handlePrestadorChange = ({

    target: { name, value },
  }: HandleInputChange) => {
    setPrestador(Number.parseInt(value));
  }


  useEffect(() => {
    let comisionUno: string[] = ['Emmanuel', 'Juan', 'Erica'];
    let comisionDos: string[] = ['Alan', 'Celeste', 'Kevin'];
    let comisionTres: string[] = ['Edgardo', 'Lucia', 'Romina'];
    if (comision === 0) {
      setPrestadores(comisionUno);
      setIdPrestadores([0, 1, 2]);
    }
    else if (comision === 1) {
      setPrestadores(comisionDos);
      setIdPrestadores([3, 4, 5]);

    }
    else {
      setPrestadores(comisionTres);
      setIdPrestadores([6, 7, 8]);

    }
    console.log(prestadores);
    console.log(comision);
  }, [comision]);

  useEffect(() => { }, [prestadores]);

  return (
    <Fragment>
      <form onSubmit={handlerNewFactura}>
        <Container fluid>
          <Row>
            <Col>
              <InputLabel handleInputChange={handleInputChange}
                factura={factura} textoLabel="Id de comision"
                placeHolder="Id de la comision"
                nombreDelCampo="IDComision"
                type="number" valor={factura.IDComision}></InputLabel>
            </Col>

            <Col>
            <InputLabel handleInputChange={handleInputChange}
                factura={factura} textoLabel="Nombre del prestador"
                placeHolder="Nombre del prestador"
                nombreDelCampo="NombrePrestador"
                type="text" valor={factura.NombrePrestador}></InputLabel>    
            </Col>
          </Row>

          <Row>
            <Col>
            <InputLabel handleInputChange={handleInputChange}
                factura={factura} textoLabel="Cuit del prestador"
                placeHolder="Cuit del prestador"
                nombreDelCampo="CuitPrestador"
                type="text" valor={factura.CuitPrestador}></InputLabel>    

              {/* <FloatingLabel
                controlId="floatingInput"
                label="Cuit del prestador"
                className="mb-3"
              >
                <Form.Control
                  type="number"
                  placeholder="Cuit del prestador"
                  name="CuitPrestador"
                  onChange={handleInputChange}
                  value={factura.CuitPrestador}
                />
              </FloatingLabel> */}
            </Col>
            <Col>
              <FloatingLabel
                controlId="floatingInput"
                label="Numero del prestador"
              >
                <Form.Control
                  type="number"
                  placeholder="Numero del prestador"
                  name="NroPrestador"
                  onChange={handleInputChange}
                  value={factura.NroPrestador}
                />
              </FloatingLabel>
            </Col>
          </Row>

          <Row>
            <Col>
              <FloatingLabel
                controlId="floatingInput"
                label="Tipo de factura"
                className="mb-3"
              >
                <Form.Control
                  type="text"
                  placeholder="Tipo de factura"
                  name="TipoFactura"
                  onChange={handleInputChange}
                  value={factura.TipoFactura}
                />
              </FloatingLabel>
            </Col>
            <Col>
              <FloatingLabel
                controlId="floatingInput"
                label="Importe facturado"
                className="mb-3"
              >
                <Form.Control
                  type="number"
                  placeholder="Importe facturado"
                  name="ImporteFacturado"
                  onChange={handleInputChange}
                  value={factura.ImporteFacturado}
                />
              </FloatingLabel>
            </Col>
          </Row>
          <Row>
            <Col>
              <FloatingLabel
                controlId="floatingInput"
                label="Numero de factura"
                className="mb-3"
              >
                <Form.Control
                  type="number"
                  placeholder="Numero de factura"
                  name="NroFactura"
                  onChange={handleInputChange}
                  value={factura.NroFactura}
                />
              </FloatingLabel>
            </Col>
            <Col>
              <SelectLabel handleInputChange={handleComisionChange} valuesDelSelec={comision}
                textoLabel="Seleccione una comision" array={["comision uno", "comision Dos", "comision tres"]}
                valorDelOption={[0, 1, 2]}></SelectLabel>
              {/* <FloatingLabel controlId="floatingSelect" label="Elige una comision">
                <Form.Select onChange={handleComisionChange} value={comision} aria-label="Elige una comision">
                  <option value={0}>Comision Uno</option>
                  <option value={1}>Comision Dos</option>
                  <option value={2}>Comision Tres</option>
                </Form.Select>
                </FloatingLabel> */}
            </Col>
          </Row>
          <Row>
            <Col>
              <SelectLabel handleInputChange={handlePrestadorChange} valuesDelSelec={prestador}
                textoLabel="Seleccione un prestador" array={prestadores}
                valorDelOption={idPrestadores}></SelectLabel>
              {/* <Form.Select   aria-label="Default select example">
                  { prestadores != null ? prestadores.map((prestador:string) =>
                   <option value={prestador}>{prestador}</option>) : null} */}
              {/* </Form.Select> */}
              {/* <option value="prestadorUno">Comision Uno</option>
                  <option value="prestadorDos">Comision Dos</option>
                  <option value="prestadorTres">Comision Tres</option> */}
            </Col>
          </Row>
          <Row>
            <Col className="d-grid gap-2">
              <Button variant="primary" size="lg" type="submit">
                Agregar
              </Button>
            </Col>
          </Row>
        </Container>
      </form>
    </Fragment>
  );

}


export default AltaFacturas;
