import { ChangeEvent, Fragment } from "react"
import { FloatingLabel, Form } from "react-bootstrap"

type HandleInputChange = ChangeEvent<HTMLSelectElement>;

interface Props {
    handleInputChange: ({       
        target : { name, value },
    }:HandleInputChange) => void;

    comision:number;

    textoLabel:string;

    array:any[];


    valorDelOption:number;

    nombreDelCampo:string;

    type:string;
} 

const SelectLabel = (props:Props)=>{
    return(
        <Fragment>
            <FloatingLabel controlId="floatingSelect" label={props.textoLabel}>
                <Form.Select onChange={props.handleInputChange} value={props.comision} aria-label={props.textoLabel}>
                { props.array != null ? props.array.map((item:string) =>
                   <option value={props.valorDelOption}>{item}</option>) : null}
                </Form.Select>
                </FloatingLabel>
        </Fragment>
    )
}

export default SelectLabel;
